'use strict'

import PropTypes from 'prop-types';
import React from 'react'
import {
  requireNativeComponent,
  UIManager,
  ViewPropTypes
} from 'react-native'

import { TimelineHolder } from "./private/TimelineHolder.js"

let _Composition = requireNativeComponent('Composition')

class Composition extends React.Component {
  static propTypes = {
    /* Native only */
    source: PropTypes.string,
    loadSync: PropTypes.bool // Force the comp to be loaded synchronously (blocking), for debugging purposes.
  };

  render() {
    return (
      <_Composition
        {...this.props}
      />
    );
  }
}

export default TimelineHolder(Composition);