import {
  NativeModules
} from 'react-native';

const DeviceInfoModule = NativeModules.DeviceInfo;

/**
 * DeviceInfo exposes information about the device the app is running on.
 */
class DeviceInfo {

  /**
   * Returns the device identifier.
   */
  static getDeviceId(): string {
    return DeviceInfoModule.deviceId;
  }

  /**
   * Returns the advertising identifier.
   */
  static getAdvertisingId(): Promise<string> {
    return DeviceInfoModule.getAdvertisingId();
  }

  /**
   * Returns the system name.
   */
  static getSystemName(): string {
    return DeviceInfoModule.systemName;
  }

  /**
   * Returns the system version.
   */
  static getSystemVersion(): string {
    return DeviceInfoModule.systemVersion;
  }

};

export default DeviceInfo;
