import { NativeModules } from 'react-native';

const FontRegistryModule = NativeModules.FontRegistry;

/**
 * A registry holding information about fonts, their files, and their styles. 
 * Fonts can be added to this registry using a name, which maps to a file and an optional style.
 * These fonts can be referred to later using the same name, and the font registry will lookup the stored information.
 */
class FontRegistry {

  /**
   * Add a font to the font registry.
   *
   * Add a font described by the provided fontInfo object to the font registry. 
   * fontInfo should contain the keys "file", "family", and optionally "style" with string values.
   * The added font can be referenced later by using the value of "family".
   *
   * @param {Object} fontInfo An object containing the font information of the font to add to the registry. For example: {file: "yi_Charter.ttc", family: "Charter", style: "Bold"}
   */
  static add(fontInfo : Object): void  {
    FontRegistryModule.add(fontInfo);
  }

  /**
   * Remove a font from the font registry.
   *
   * Remove a font from the registry using the font family name.
   *
   * @param {string} fontFamilyName The font family name that was used to register the font in the registry.
   */
  static remove(fontFamilyName : string): void {
    FontRegistryModule.remove(fontFamilyName);
  }

  /**
   * Remove all fonts from the registry.
   */
  static clear(): void  {
    FontRegistryModule.clear();
  }

  /**
   * Verify if a font exists in the font registry.
   *
   * Verify if a font exists in the registry using the font family name.
   *
   * @param {string} fontFamilyName The font family name that was used to register the font in the registry.
   *
   * @return {Promise<any>} Returns a promise containing an Object with a boolean "exists" signifying if the font exists, and a string "fontFamily" for the font family name "exists" refers to.
   */
  static exists(fontFamilyName : string): Promise<any> {
    return FontRegistryModule.exists(fontFamilyName);
  }
};

export default FontRegistry;