'use strict'

import PropTypes from 'prop-types';
import React, {Component} from 'react'
import {
  requireNativeComponent,
  UIManager
} from 'react-native'

import { FocusableRef } from "./private/FocusableRef.js"
import { TimelineHolder } from "./private/TimelineHolder.js"

let _SliderRef = requireNativeComponent('YiScrollBarView')

class SliderRef extends React.Component {

  constructor(props) {
    super(props);
  }

  _sendDirectEvent(func, event) {
    if (func) {
      func(event.nativeEvent.value);
    }
  }

  _onValueChange = (event) => {
    this._sendDirectEvent(this.props.onValueChange, event);
  }

  _onSlidingComplete = (event) => {
    this._sendDirectEvent(this.props.onSlidingComplete, event);
  }

  render() {
    return (
      <_SliderRef
        {...this.props}
        onValueChange={this._onValueChange}
        onSlidingComplete={this._onSlidingComplete}
      />
    );
  }
}

export default TimelineHolder(FocusableRef(SliderRef));
